mapper.update:
UPDATE article
SET view_count = view_count + 1
WHERE id = #{id}

UPDATE article
SET like_count = like_count + #{i}
WHERE id = #{detailsId}

UPDATE article
SET star_count = star_count + #{i}
WHERE id = #{detailsId}

mapper.select:
SELECT count(*)
FROM article_likes
WHERE article_id = #{detailsId} AND user_id = #{userId}

SELECT count(*)
FROM article_stars
WHERE article_id = #{detailsId} AND user_id = #{userId}

SELECT content
FROM tag_content
WHERE type_id = #{id}
